void integerFactorization(int n, vector<int>& base, vector<int>& exp)
{
    base.clear();
    exp.clear();
    int a = 2;
    while(a * a <= n){
        int b = 0;
        while(n % a == 0){
            ++ b;
            n /= a;
        }
        if(b > 0){
            base.push_back(a);
            exp.push_back(b);
        }
        ++ a;
    }
    if(n > 1 || base.size() == 0){
        base.push_back(n);
        exp.push_back(1);
    }
}

double solve(int n)
{
    vector<int> base, exp;
    integerFactorization(n, base, exp);
    int m = base.size();

    int ret = 1;
    int same = 1;
    for(int i=0; i<m; ++i)
        ret *= 2 * exp[i] + 1;
    ++ ret;
    ret /= 2;
    return ret;
}

int main()
{
    for(;;){
        int n;
        cin >> n;
        if(n == 0)
            break;
        cout << solve(n) << endl;
    }

    return 0;
}

