        for(i=0;n!=1 && i<s;i++){
            if(n%ps[i]==0){
                int cnt=1;
                n/=ps[i];
                while(n%ps[i]==0){
                    n /= ps[i];
                    cnt++;
                }
                es.PB(cnt);
            }
        }
        if(i==s && n!=1) es.PB(1);
        ll ans=1;
        for(i=0;i<SZ(es);i++){
            ans *= (2*es[i]+1);
        }
        cout << ans/2 + 1 << endl;
    }
    return 0;
}

