        if ( n % i == 0 ) {
            res.insert(i);
            if ( i != n/i ) res.insert(n/i);
        }
    }
    return res;
}

int count( SI D_ ) {
    int res = 0;
    VL D( D_.begin(), D_.end() ); 
    for ( VL::iterator it_i = D.begin(); it_i + 1 != D.end(); ++ it_i ) {
        if ( *it_i == L ) continue;
        for ( VL::iterator it_j = it_i + 1; it_j != D.end(); ++ it_j ) {
            if ( *it_j == L ) continue;
            if ( lcm( *it_i, *it_j ) == L ) res ++;
        }
    }
    return res;
}

int solve() {
    if ( L == 1 ) return 1;
    SI D = getDivisors(L);
    return D.size() + count(D);
}

int main() {
    make();
    while ( cin >> L && L ) {
        cout << solve() << endl;
    }
    return 0;
}

