map<int,int> decomposition(ll L){
	map<int,int> cnt;
	for(ll i = 2; i*i <= L; i++){
		if(L%i==0){
			while(1){
				if(L%i==0){
					cnt[i]++;
					L/=i;
				}
				else
					break;
			}
		}
