typedef long long LL;
const int SIZE = 1000000;
LL L;
bool isPrime[SIZE];
LL P[SIZE];
int PC;
int E[SIZE]; 

void makePrimes() {
    PC = 0;
    fill( P, P+SIZE, false );
    for ( int i = 3; i < SIZE; i += 2 ) {
        isPrime[i] = true; 
    }
    P[PC++] = 2;
    for ( int i = 3; i < SIZE; i += 2 ) {
        if ( ! isPrime[i] ) continue;
        P[PC++] = i;
        for ( int j = i + i; j < SIZE; j += i ) {
            isPrime[j] = false;
        }
    }
}

void init() {
    fill( E, E+SIZE, 0 );
}

void calcExp( LL L ) {
    for ( int i = 0; i < PC && L > 1; ++ i ) {
        LL p = P[i];
        while ( L > 1 && L % p == 0 ) {
            E[i] ++;
            L /= p;
        }
    }
}

int dfs( int k, int r ) {
    if ( k >= PC ) return r;
    int res = 0;
    for ( int i = 0; i < E[k]; ++ i ) res += dfs( k+1, r );
    return res + dfs( k+1, r*(E[k]+1) );
}

int solve() {
    calcExp(L);
    return (dfs( 0, 1 )+1)/2;
}

int main() {
    makePrimes();
    while ( cin >> L && L ) {
        init();
        cout << solve() << endl;
    }
    return 0;
}

