template<typename Integer>
Integer divisorFunction1(Integer n, vector<Integer> &vdivs){
  Integer ret=0;
  if(n==1)return 1;
  stack<pair<Integer,Integer> > st;
  vector< pair<Integer,Integer> > facts;
  Factorization(n,facts);
  st.push(make_pair(0,1));
  while(!st.empty()){
    pair<Integer,Integer> nst=st.top();
    st.pop();
    if(nst.first>=facts.size()){
      vdivs.push_back(nst.second);
      continue;
    }
    Integer r=1;
    for(int i = 0; i < facts[nst.first].second+1; ++i){
      pair<Integer,Integer> nx(nst.first+1,nst.second*r);
      st.push(nx);
      r*=facts[nst.first].first;
    }
  }
  return ret;
}

