ll L;

ll calc(ll x,ll y){
	if(y == 0){
		return x;
	}else{
		return calc(y,x%y);
	}
}

void func(){

	if(L == 1){
		printf("1\n");
		return;
	}

	vector<ll> DIVISOR;

	for(ll i = 1; i <= sqrt(L); i++){
		if(L%i == 0){
			DIVISOR.push_back(i);
			if(i*i != L)DIVISOR.push_back(L/i);
		}
	}

	sort(DIVISOR.begin(),DIVISOR.end());

	ll small,big,common;

	int ans = 0;

	for(int i = 0; i < DIVISOR.size(); i++){
		for(int k = i; k < DIVISOR.size(); k++){
			small = DIVISOR[i];
			big = DIVISOR[k];

			common = calc(big,small);

			small /= common;
			big /= common;

			if(small*big*common == L)ans++;
		}
	}

	printf("%d\n",ans);
}

