template <class T>
map<T, int> prime_factors(T n)
{
	map<T, int> res;
	for (T i = 2; i * i <= n; ++i)
	{
		if (n % i == 0)
		{
			int c = 0;
			for ( ; n % i == 0; n /= i)
				++c;
			res[i] = c;
		}
	}
	if (n > 1)
		res[n] = 1;
	return res;
}
template <class T>
vector<T> divisors(T n)
{
	vector<T> res;
	for (T i = 1; i * i <= n; ++i)
	{
		if (n % i == 0)
		{
			res.push_back(i);
			if (i != n / i)
				res.push_back(n / i);
		}
	}
	return res;
}
vector<int> fact_num, take;
ll dfs(int depth)
{
	if (depth == take.size())
	{
		ll res = 1;
		for (int i = 0; i < take.size(); ++i)
			if (take[i] == fact_num[i])
				res *= fact_num[i] + 1;
		return res;
	}
	else
	{
		ll res = 0;
		for (int i = 0; i <= fact_num[depth]; ++i)
		{
			take[depth] = i;
			res += dfs(depth + 1);
		}
		return res;
	}
}
int main()
{
	ll L;
	while (scanf("%lld", &L), L)
	{
		map<ll, int> f = prime_factors(L);
		fact_num = vector<int>();
		foreach (it, f)
			fact_num.push_back(it->second);
		take.resize(fact_num.size());

		printf("%lld\n", (dfs(0) + 1) / 2);
	}
}

