map<ll,int> prime_factorization(ll n){
	map<ll,int> ret;

	for(ll div=2;div<=1+sqrt(n);div++){
		while(n%div==0){
			ret[div]++;
			n/=div;
		}
	}
	if(n!=1)ret[n]++;
	return ret;
}

