#include <stdio.h>

int euc(int n, int m) /* ツδーツクツδ環ッツドツづ個古敖渉慊法ツ　*/
{
	if (n == 0)
		return m;
	else if(m % n == 0)
		return n;
	else
		euc(m % n, n);
}


int main(void)
{
	int in;
	int a, b;

	int gcd = 0;
	int smp = 0;

	int out[1000] = {0};
	int times = 0;

	while(1){

		scanf("%d",&in);
		if(in == 0)
			break;

		for( a = 1 ; a <= in ; a++){
			for( b = a ; b <= in ; b++)
			{
				gcd = euc(a,b);

				if( a * b / gcd == in)
					out[times]++;
			}
		}

		times++;

	}


	for( a = 0 ; a < times ; a++ ){
	printf("%d\n", out[a]);	}

	return 0;
}

