}

void MakePrime(){
  for(int i=0;i<MAX;++i)
    isPrime[i]=true;
  isPrime[0]=false;
  isPrime[1]=false;
  for(long long int i=2;i<MAX;++i)
    if(isPrime[i])
      for(long long int j=2;i*j<MAX;++j)
	isPrime[i*j]=false;
  for(int i = 0; i < MAX; ++i)
    if( isPrime[i] )
      vprimes.push_back( i );
}

template<typename Integer>
void Factorization(Integer n, vector< pair<Integer,Integer> > &vp){
  if(n==1){vp.push_back(make_pair(1,1));return ;}
  for(int i = 0; i < vprimes.size(); ++i){
    while(n%vprimes[i]==0){
      n/=vprimes[i];
      if( vp.size()>0 && vp.back().first == vprimes[i] )
	vp.back().second++;
      else
	vp.push_back( make_pair(vprimes[i],1) );
    }
    if(n==1)return ;
  }
  vp.push_back( make_pair(n,1) );  
}

