void partitions(int n, int max_n, vector<int>& v, vector<vector<int> >& vv) {
  if(n==0){vv.push_back(v);return;}
  for(int i=min(n,max_n);i>0;i--){v.push_back(i);partitions(n-i,i,v,vv);v.pop_back();}
}

