int64 sieve(int64 n) { //豎ょ?1..n 逧?エ?焚1..num 邏?焚譬?ョー莉・蜿頑ャァ諡牙?謨ー
	int64 p = 0;
	memset(isprime, 1, sizeof(isprime));
	for (int64 i = 2; i <= n; i++) {
		if (isprime[i]) {
			prime[p++] = i;
			phi[i] = i - 1;
		}
		for (int64 j = 0; j < p && i * prime[j] <= n; j++) {
			isprime[i * prime[j]] = 0;
			if (i % prime[j] == 0) {
				phi[i * prime[j]] = phi[i] * prime[j];
				break;
			} else
				phi[i * prime[j]] = phi[i] * (prime[j] - 1);
		}
	}
	isprime[0] = isprime[1] = 0;
	return p;
}

int64 factorize(int64 x, int64 factor[], int64 cnt[]) {
	int64 ret = 0;
	for (int64 i = 0; i < num && prime[i] <= x; i++) {
		cnt[i] = 0;
		if (x % prime[i] == 0) {
			factor[ret++] = i;
			while (x % prime[i] == 0) {
				cnt[i]++;
				x /= prime[i];
			}
		}
	}
	return ret;
}


