void func(){

	vector<ll> DIVISOR;

	for(ll i = 1; i <= sqrt(L); i++){
		if(L%i == 0){
			DIVISOR.push_back(i);
			DIVISOR.push_back(L/i);
		}
	}

	ll small,big,common;

	int ans = 0;

	for(int i = 0; i < DIVISOR.size()-1; i++){
		for(int k = i+1; k < DIVISOR.size(); k++){
			small = DIVISOR[i];
			big = DIVISOR[k];

			common = calc(big,small);

			small /= common;
			big /= common;

			if(small*big*common == L)ans++;
		}
	}


	printf("%d\n",ans+1);
}

int main(){

	while(true){
		scanf("%lld",&L);
		if(L == 0)break;

		func();
	}

	return 0;
}

