#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

map<ll, ll> prime_factor(ll n)
{
  map<ll, ll> res;
  for (ll i = 2; i * i <= n; i++) {
    while (n % i == 0) {
      ++res[i];
      n /= i;
    }
  }
  if (n != 1) {
    res[n] = 1;
  }
  return res;
}

ll lcm(ll a, ll b)
{
  return a / __gcd(a, b) * b;
}

ll sum(ll n)
{
  return (n * (n + 1) / 2);
}

ll calc(int r) {
  ll res = 0;
  for(int p=0; p<=r; p++) {
    for(int q=0; q<=r; q++) {
      if(p + q >= r + min(p, q)) {
	res ++;
      }
    }
  }
  return res;
}

int main()
{
  ll L;
  while (cin >> L, L) {
    auto pf = prime_factor(L);
    ll res = 1;
    for (auto &p: pf) {
      res *= calc(p.second);
      //      res *= sum(p.second + 1);
    }    
    cout << res/2 + 1 << endl;
  }
  return 0;
}

