typedef long long ll;
bitset<1000001> pt;
vector<int> pa;

void MakeTable(int n){
  pt.reset();
  pt.set(2);

  int i = 2;
  while(++i<=n)if(i&1)pt.set(i);

  i = 3;
  while(i*i<=n){
    for(int j=3*i;j<n;j+=2*i)pt.reset(j);
    while(!pt[++i]);
  }
}

void MakeArray(int n){
  MakeTable(n);
  for(int i=0;i<=n;i++)
    if(pt[i])pa.push_back(i);
}

vector<int> e,te;

int rec(int x){
  if(x==(int)e.size()){
    int res = 1;
    for(int i=0;i<(int)e.size();i++)
      if(e[i]==te[i])res *= (e[i]+1);
    return res;
  }else{
    int res = 0;
    for(int i=0;i<=e[x];i++){
      te.push_back(i);
      res += rec(x+1);
      te.pop_back();
    }
    return res;
  }
}

int main(){
  ll l;
  MakeArray(1000000);
  while(scanf("%lld",&l) && l){
    int j = 0;
    e.clear();
    while(j<(int)pa.size() && pa[j]<=l){
      int cnt = 0;
      while(!(l%pa[j])){
	l/=pa[j];
	cnt++;
      }
      if(cnt)e.push_back(cnt);
      j++;
    }
    if(l>1)e.push_back(1);
    te.clear();
    int ans = rec(0);
    printf("%d\n",(ans+1)/2);
  }
}

