int pn(int a[],int n){
  int i,j;
  a[0]=a[1]=0;
  for(i=2;i<n;i++)
    a[i]=i;
  for(i=2;i*i<n;i++){
    if(a[i]){
      for(j=i*i;j<n;j+=i)
        a[j]=0;
    }
  }
  j=2;
  for(i=0;j<n;i++){
    a[i]=a[j];
    for(j++;j<n&&a[j]==0;j++);
  }
  return i;
}
int main(){
  int i,j;
  int a[1000000];
  a[pn(a,1000000)]=1000001;
  long long n,m;
  while(cin>>n&&n){
    m=n;
    vector<long long> b;
    b.reserve(900);
    b.push_back(1);
    for(i=0;(long long)a[i]*a[i]<=m;i++){
      if(n%a[i]==0){
	int ln=b.size();
	long long pi=a[i];
	do{
	  for(j=0;j<ln;j++)
	    b.push_back(b[j]*pi);
	  n/=a[i];
	  pi*=a[i];
	}while(n%a[i]==0);
      }
    }
    if(n!=1){
      int ln=b.size();      
      for(j=0;j<ln;j++)
	b.push_back(b[j]*n);
    }
    int ct=0;
    for(i=0;i<(int)b.size();i++){
      for(j=i;j<(int)b.size();j++){
	if(b[i]/__gcd(b[i],b[j])*b[j]==m)
	  ct++;
      }
    }
    cout<<ct<<endl;
  }
  return 0;
}

