const long long int  MAX  = 1000001;
bool p[MAX];
using namespace std;

vector<long long int > primeFactor( long long int  n ){
  vector<long long int > v;
  rep(i, sqrt(n)+1){
    if(!p[i])continue; 
    if( n % i != 0 )continue;
    long long int  cnt = 1;
    n /= i;
    while( n % i == 0){
      n /= i;
      cnt++;
    }
    v.push_back(cnt);
  }  
  if( n != 1 ) v.push_back(1);
  return v;
}

long long int  solve(vector<long long int > &pf, long long int  now, vector<long long int > &comb){
  if(now == pf.size()){
    long long int  tmp = 1;
    rep(i, pf.size()){
      if(pf[i] == comb[i]){
	tmp *= pf[i] + 1;
      }
    }
    return tmp;
  }
  long long int  sum = 0;
  rep(i, pf[now] + 1){
    comb.push_back(i);
    sum += solve(pf, now+1, comb);
    comb.pop_back();
  }
  return sum;
}

main(){
  rep(i, MAX)p[i] = true;
  p[0] = p[1] = false;
  rep(i, sqrt(MAX)){
    if(!p[i])continue;
    for(long long int  j = 2; j * i < MAX; j++){
      p[i*j] = false;
    }
  }

  long long int  l;
  while( cin >> l ){
    if( l == 0)break;
    vector<long long int > pf = primeFactor(l), comb;
    cout << ( solve(pf, 0, comb) + 1) / 2 << endl;
  }
}

