map<int,int> prime_factorization(int n){
	map<int,int> ret;

	for(int div=2;div<=1+sqrt(n);div++){
		while(n%div==0){
			ret[div]++;
			n/=div;
		}
	}
	if(n!=1)ret[n]++;
	return ret;
}

