  int primes[100];
  int shurui,ret;

  while(scanf("%lld",&n) && n)
  {
    soinsuu_bunkai(n,primes,&shurui);

    ret=solve(primes,shurui);
    printf("%d\n",ret);
  }
  return(0);
}

