class Sol{
	public void Solve(){
		if(L==1){
			Console.WriteLine(1);
			return;
		}

		Dictionary<long,long> pr=PrimDec(L);
		Dictionary<long,long> highest=new Dictionary<long,long>();
		foreach(long k in pr.Keys){
			highest.Add(k,(long)Math.Pow(k,pr[k]));
		}


		long Total=0;
		for(long i=1;i*i<=L;i++){
			if(L%i!=0)continue;
			long j=L/i;

			long cnt=1;
			foreach (long k in highest.Keys){
				if(i%highest[k]==0){
					cnt*=(pr[k]+1);
				}
			}
			Total+=cnt;

			if(j==i)continue;
			cnt=1;
			foreach (long k in highest.Keys){
				if(j%highest[k]==0){
					cnt*=(pr[k]+1);
				}
			}
			Total+=cnt;
		}

		long Ans=(Total-1)/2+1;//(L,L)以外は必ず2回カウントされる
		Console.WriteLine(Ans);
	}
	long L;
	public Sol(long l_){
		L=l_;
	}

	Dictionary<long,long> PrimDec(long A){
	 	Dictionary<long,long> D=new Dictionary<long,long>();
	 	if(A==1)return null;
	 	long rem=A;
