class Sol{
	public void Solve(){
		if(L==1){
			Console.WriteLine(1);
			return;
		}

		Dictionary<long,long> pr=PrimDec(L);
		Dictionary<long,long> highest=new Dictionary<long,long>();
		foreach(long k in pr.Keys){
			highest.Add(k,(long)Math.Pow(k,pr[k]));
		}


		long Total=0;
		for(long i=1;i*i<=L;i++){
			if(L%i!=0)continue;
			long j=L/i;

			long cnt0=1;
			long cnt1=1;
			foreach (long k in highest.Keys){
				if(i%highest[k]==0){
					cnt0*=(pr[k]+1);
					continue;//iが最高次を割り切るならjは割り切らない
				}
				if(j%highest[k]==0){
					cnt1*=(pr[k]+1);
				}
			}
			Total+=cnt0;
			if(i!=j)Total+=cnt1;
		}

		long Ans=(Total-1)/2+1;//(L,L)以外は必ず2回カウントされる
		Console.WriteLine(Ans);
	}
	long L;
	public Sol(long l_){
		L=l_;
	}

	Dictionary<long,long> PrimDec(long A){
	 	Dictionary<long,long> D=new Dictionary<long,long>();
	 	if(A==1)return null;
	 	long rem=A;
	 	for(long i=2;i<=(long)Math.Sqrt((double)A);i++){
	 		if(rem%i==0){
	 			long cnt=0;
	 			while(rem%i==0){cnt++;rem/=i;}
	 			D.Add(i,cnt);
	 		}
	 		if(rem==1)break;
	 	}
	 	if(rem!=1)D.Add(rem,1);
	 	return D;
	}



