	cin >> x;
	if(max < x) max = x;
	onedata.time.push_back(x);
      }
      data.push_back(onedata);
      onedata.time.clear();
    }

    sort(data.begin(), data.end(), SortName);

    for(i=0; i<=max; ++i){
      for(j=0; j<data.size(); ++j){
	iter = find(data[j].time.begin(), data[j].time.end(), i);
	if(iter != data[j].time.end())
	  check.push_back(j);
      }

      if(check.size() == n){
	for(j=0; j<data.size(); ++j)
	  ++data[j].point;
      }else if(check.size() != 0){
	for(j=0; j<check.size(); ++j)
	  data[check[j]].point += (n + 1 - check.size());
      }
      check.clear();
    }

    for(i=0, minpoint=1000; i<data.size(); ++i){
      if(minpoint > data[i].point){
	answername = data[i].name;
	minpoint = data[i].point;
      }
    }

    cout << minpoint << " " << answername << endl;
    data.clear();
  }

  return 0;
}

bool SortName(const DATA& x, const DATA& y){
  return x.name < y.name;
}

