using namespace std;

double compute_e(int n, int m, int v[], double r[]) {
    double e = 0;
    double normalizing = 0;
    for (int i = 0; i < m; ++i) {
        e += v[i] * r[i];
        normalizing += r[i];
    }
    e /= normalizing;
    return e;
}

int main() {
