    double e_leader = 0;
    double e_max = -1;
    cin >> t;
    for (int i = 0; i < t; ++i) {
        cin >> n >> m;
        for (int j = 0; j < m; ++j) {
            cin >> v[j] >> r[j];
        }
        e_max = max(e_max, compute_e(n, m, v, r));
    }
    cin >> p >> q;
    for (int i = 0; i < q; ++i) {
        cin >> v[i] >> r[i];
    }
    e_leader = compute_e(p, q, v, r);
    if (e_max > e_leader + 0.0000001) {
        cout << "YES" << endl;
    } else {
        cout << "NO" << endl;
    }
    return 0;
}

