#include <cstdio>
#include <cassert>
#include <cstdint>
#include <vector>
#include <algorithm>
#include <string>
#include <stdexcept>

// using Tsurai()=std::out_of_range;
struct Tsurai: public std::out_of_range {
  Tsurai(): std::out_of_range("") {}
};

void apply(int &lhs, char op, int rhs) {
  switch (op) {
  case '+':
    lhs += rhs;
    break;
  case '-':
    lhs -= rhs;
    break;
  case '*':
    lhs *= rhs;
    break;
  }
  if (!(0 <= lhs && lhs < 1024))
    throw Tsurai();
}

int parse_bin(const std::string &s, size_t &i) {
  int res=0;
  while (i < s.length()) {
    res = res*10+s[i]-'0';
    ++i;
    if (res >= 1024) throw Tsurai();
  }
  assert(res < 1024);
  return res;
}

int parse(
    const std::string &s, size_t &i,
    const std::vector<std::string> &ops={"+-", "*", ""}, size_t prec=0) {

  if (prec == ops.size()) {
    if (s.at(i) == '(') {
      ++i;
      if (s.at(i) == ')') throw Tsurai();
      size_t j=i;
      (void)parse_bin(s, j);
      if (s.at(j) == ')') throw Tsurai();

      int res=parse(s, i, ops, 0);
      ++i;
      if (s.at(i) != ')')
        throw Tsurai();
      return res;
    }
    if (isdigit(s.at(i))) {
      return parse_bin(s, i);
    }
    throw Tsurai();
  }

  int lhs=parse(s, i, ops, ++prec);
  while (true) {
    char op=s.at(i);
    if (!(op == '+' || op == '-' || op == '*' || op == ')'))
      throw Tsurai();

    if (!std::count(ops[prec].begin(), ops[prec].end(), op))
      break;

    int rhs=parse(s, ++i, ops, prec+1);
    apply(lhs, op, rhs);
  }
  return lhs;
}

int rec(std::string &s, std::vector<size_t> &dot) {
  if (dot.empty()) {
    try {
      size_t i=0;
      return parse(s, i);
    } catch (Tsurai) {
      return -1;
    }
  }

  size_t x=dot.back();
  dot.pop_back();
  int res=-1;
  for (char ch: {'0', '1', '+', '-', '*', '(', ')'}) {
    s[x] = ch;
    res = std::max(res, rec(s, dot));
  }
  s[x] = '.';
  dot.push_back(x);
  return res;
}

int main() {
  char buf[128];
  scanf("%s", buf);
  std::string s=buf;
  std::vector<size_t> dot;
  for (size_t i=0; i<s.length(); ++i) {
    if (s[i] == '.')
      dot.push_back(i);
  }

  printf("%d\n", rec(s, dot));
}

