    }
  }

  for (int j = 0; j < 50; j++) {
    int count = 0;
    for (int i = 0; i < n; i++) {
      if (times[i][j] == 1)
        count++;
    }
    for (int i = 0; i < n; i++) {
      if (times[i][j] == 1)
        points[i] += (n - count + 1);
    }
  }

