      point[i]=0;
    }
    minp=(1e9);
    sort(t,t+n);
    for(int i=0;i<n;i++){
      for(int j=0;j<t[i].size();j++){
	int idx=t[i][j].second;
	point[i]+=n-cnt[idx]+1;
      }
      if(t[i].size()==0)point[i]=(1e9);
      minp=min(minp,point[i]);
    }
    for(int i=0;i<n;i++){
      if(minp==point[i]){
	cout<<minp<<' '<<t[i][0].first<<endl;
	break;
      }
    }
    for(int i=0;i<n;i++)t[i].clear();
  }
  return 0;
}

