class person{
	public:
	person(string strarg, int intarg){
		name = strarg;
		point = intarg;
	}
	string name;
	int point;

	bool operator <(const person arg) const{
		if(point != arg.point){
			return point < arg.point;
		}else{
			return name < arg.name;
		}
	}
};

