struct data{
  string name;
  vector<int> view;
  int score;
  data():score(0){}
  bool operator < (const data& d) const {
    if(score != d.score) return score < d.score;
    return name < d.name;
  }
};
const int MAX = 30;
int disp[MAX];
int n;
vector<data> V;

void init(){
  V.clear();
  fill(disp,disp+MAX,0);
}

void input(){
  for(int i = 0; i < n; i++){
    int num;
    data d;
    cin >> d.name;
    cin >> num;
    while(num--){
      int in;
      cin >> in;
      d.view.push_back(in);
      disp[in]++;
    }
    V.push_back(d);
  }
}

void solve(){

  for(int i = 0; i < (int)V.size(); i++)
    for(int j = 0; j < (int)V[i].view.size(); j++)
      V[i].score += n-disp[V[i].view[j]]+1;

  sort(V.begin(),V.end());

  cout << V[0].score << " " << V[0].name << endl;

}

int main(){

  while(cin >> n && n){
    init();
    input();
    solve();
  }
  return 0;
}

