using namespace std;

struct Info{
	Info(){
		point = 0;
	}
	char name[11];
	int point;
};

struct Data{
	Data(){
		index = 0;
	}
	int table[35],index;
};

int N;

bool strCmp(char* base, char* comp){
	int length1 = 0,length2 = 0;
	for(int i=0;base[i] != '\0'; i++)length1++;
	for(int i=0;comp[i] != '\0'; i++)length2++;
	if(length1 != length2)return false;

	for(int i=0;base[i] != '\0'; i++){
		if(base[i] != comp[i])return false;
	}
	return true;
}

void strcpy(char* to,char* str){
	for(int i=0;str[i] != '\0';i++){
		to[i] = str[i];
		to[i+1] = '\0';
	}
}

int strCmp2(char* left,char* right){
	int i;

	if(strCmp(left,right))return 3;

	for(i=0;left[i] != '\0' && right[i] != '\0'; i++){
		if(left[i] != right[i]){
			if(left[i] < right[i])return 1;
			else{
				return 2;
			}
		}
	}
	if(left[i] == '\0')return 1;
	else{
		return 2;
	}
}

void func(){
	Info info[N],work;
	Data data[31];

	int num,tmp;

	for(int i = 0; i < N; i++){
		scanf("%s %d",info[i].name,&num);
		for(int k = 0; k < num; k++){
			scanf("%d",&tmp);
			data[tmp].table[data[tmp].index++] = i;
		}
	}

	for(int i = 0; i <= 30; i++){
		tmp = N - (data[i].index-1);
		for(int k = 0; k < data[i].index;k++){
			info[data[i].table[k]].point += tmp;
		}
	}

	for(int i = 1; i <= N-1; i++){
		for(int k = N-1; k >= i; k--){
			if((info[k].point < info[k-1].point) || (info[k].point == info[k-1].point && strCmp2(info[k].name,info[k-1].name) == 1)){
				strcpy(work.name,info[k-1].name);
				work.point = info[k-1].point;
				strcpy(info[k-1].name,info[k].name);
				info[k-1].point = info[k].point;
				strcpy(info[k].name,work.name);
				info[k].point = work.point;
			}
		}
	}

	printf("%d %s\n",info[0].point,info[0].name);

}

int main(){

	while(true){
		scanf("%d",&N);
		if(N == 0)break;

		func();
	}

	return 0;
}

