typedef pair< int, string > result;
typedef map< string, int > Points;

int n;
string names[22];
vector< Array > md;

void solve() {
	vector< SArray > times(31, SArray());

	for_(i,0,n) {
		int m = size_of(md[i]);
		for_(j,0,m) {
			times[ md[i][j] ].push_back(names[i]);
		}
	}

	Points points;

	for_(t,0,31) {
		int m_size = size_of(times[t]);
		int point = n - m_size + 1;

		for_(i,0,m_size) {
			points[ times[t][i] ] += point;
		}
	}

	vector< result > vr;
	for_(i,0,n) {
		vr.push_back( result(points[names[i]], names[i]));
	}

	sort(allof(vr));

	cout << vr[0].first << " " << vr[0].second << endl;
}

int main() {
	while (cin >> n, n) {
		md.assign(n, Array());
		for_(i,0,n) {
			int m; cin >> names[i] >> m;
			for_(j,0,m) {
				int d; cin >> d;
				md[i].push_back(d);
			}
		}
		solve();
	}
	return 0;
}

