#include <iostream>
#include <string>
#include <algorithm>
#define T_MAX 31
#define N_MAX 21
using namespace std;


struct Pdata{
  string name;
  int point;
  int m; 
};

bool operator < ( const Pdata &p, const Pdata &q ){
  if( p.point == q.point ){
    return p.name < q.name;
  }else{
    return p.point < q.point;
  }
}

int main () {
  while ( 1 ) {
    int n;
    cin >> n;
    if( !n ) { return 0; }
    int table[ T_MAX ] = { 0 };
    int ds[ N_MAX ][ T_MAX ] = { 0 };
    Pdata p[ N_MAX ];

    for( int i = 0; i < n; i++ ) {
      cin >> p[ i ].name >> p[ i ].m;
      for( int j = 0; j < p[ i ].m; j++ ) {
        cin >> ds[ i ][ j ];
        table[ ds[ i ][ j ] ]++;
      }
    }
    for( int i = 0; i < n; i++ ) {
      p[ i ].point = 0;
      for( int j = 0; j < p[ i ].m; j++ ) {
        p[ i ].point += ( n - table[ ds[ i ][ j ] ] + 1 );
      }
    }
    sort( p, p + n );
    cout <<  p[ 0 ].point << " " << p[ 0 ].name << endl;
  }

}

