#include<iostream>
using namespace std;

int numofbits(long bits)
{
  bits = (bits & 0x55555555) + (bits >> 1 & 0x55555555);
  bits = (bits & 0x33333333) + (bits >> 2 & 0x33333333);
  bits = ((bits >> 4) + bits) & 0x0f0f0f0f;
  bits += bits >> 8;
  return (bits + (bits >> 16)) & 0xff;
}

int main()
{
  int n;
  string name[20];
  int m[20],d[31];

  while(cin>>n,n){
    int num[30]={0},point[30]={0};
    for(int i=0;i<n;i++){
      cin>>name[i]>>m[i];
      for(int j=0;j<m[i];j++){
	cin>>d[j];

	num[d[j]] ^= (1<<i);
      }
    }

    int add;
    for(int k=0;k<30;k++){
      add = n - numofbits(num[k]) + 1;
      for(int i=0;i<n;i++){
	if(num[k] & (1<<i))point[i] += add;
      }
    }

    int ans=0;
    for(int i=0;i<n;i++){
      if(point[ans]>point[i]){
	ans=i;
      }else if(ans==point[i]){
	if(name[ans]>name[i])ans=i;
      }
    }
    cout<<point[ans]<<" "<<name[ans]<<endl;
  }
}

