int main()
{
    int n;

    while (scanf("%d", &n) && n){
        pair<int, string> info[32];
        int b[32];

        for (int i = 0; i < n; i++){
            char tmp[32];
            scanf("%s", tmp);
            info[i].first = 0;
            info[i].second = (string)tmp;

            b[i] = 0;

            int m;
            scanf("%d", &m);
            for (int j = 0; j < m; j++){
                int x;
                scanf("%d", &x);
                b[i] |= (1 << x);
            }
        }

        for (int i = 0; i < 30; i++){
            int base = n + 1;
            int cnt = 0;
            for (int j = 0; j < n; j++){
                if (b[j] >> i & 1) cnt++;
            }
            for (int j = 0; j < n; j++){
                if (b[j] >> i & 1) info[j].first += base - cnt;
            }
        }

        sort(info, info + n);

        printf("%d %s\n", info[0].first, info[0].second.c_str());
    }

    return (0);
}

