      }
    }
    int mini = (2 << 20); string name;
    for(itr=d.begin();itr!=d.end();itr++){
      int sum = 0;
      for(int i=0;i<(itr->second).size();i++){
        int cnt = 0;
        for(map<string,vector<int> >::iterator it=d.begin();it!=d.end();it++){
          if(it == itr) continue;
          for(int j=0;j<(it->second).size();j++){
