				}
			}
			int index = 0;
			int min = Integer.MAX_VALUE;
			for (int i = 0; i < n; i++) {
				int p = 0;
				for (int j = 0; j < 30; j++) {
					if (act[i][j]) {
						p += n - ns[j] + 1;
					}
				}
				if (min > p) {
					min = p;
					index = i;
				} else if (min == p) {
					if (names[i].compareTo(names[index]) > 0) {
						index = i;
					}
				}
			}
			System.out.println(min + " " + names[index]);
		}
	}
