class MyMath{
	/**
	 * 弧度法の角度を入力してsinの値を返す（Math.sin の入力はラジアン）
	 */
	public static double sin(int r){return Math.sin(Math.toRadians(r));}

	/**
	 * 弧度法の角度を入力してcosの値を返す（Math.sin の入力はラジアン）
	 */
	public static double cos(int r){return Math.cos(Math.toRadians(r));}
	public static int max(int a, int b){return Math.max(a, b);}
	public static int min(int a, int b){return Math.min(a, b);}
	public static boolean isCross(int x1, int y1, int x2, int y2, int x3, int y3, int x4, int y4){
	    // 並行な場合
	    int m = (x2-x1)*(y4-y3)-(y2-y1)*(x4-x3);
	    if(m == 0) return false;
	    // 媒介変数の値が0より大きく1以下なら交差する、これは問題の状況によって変わるかも。
	    double r =(double)((y4-y3)*(x3-x1)-(x4-x3)*(y3-y1))/m;
	    double s =(double)((y2-y1)*(x3-x1)-(x2-x1)*(y3-y1))/m;
	    return (0 < r && r <= 1 && 0 < s && s <= 1);
	}
	public static boolean isParallel(int x1, int y1, int x2, int y2, int x3, int y3, int x4, int y4){
	    if((x2-x1)*(y4-y3) == (y2-y1)*(x4-x3)) return true;
	    else return false;
	}
	public static double sq(double d){return d*d;}
	public static int sq(int i){return i*i;}
	public static int sqdist(int x1, int y1, int x2, int y2){
		return sq(x1-x2) + sq(y1-y2);}
	public static double sqdist(double x1, double y1, double x2, double y2){
		return sq(x1-x2) + sq(y1-y2);}
	public static double dist(int x1, int y1, int x2, int y2){
		return Math.sqrt(sqdist(x1, y1, x2, y2));}
	public static double dist(double x1, double y1, double x2, double y2){
		return Math.sqrt(sqdist(x1, y1, x2, y2));}
	long comb(long n, long m){
		if(n < m) return 0;
    	long c = 1; m = (n - m < m ? n - m : m);
    	for(long ns = n - m + 1, ms = 1; ms <= m; ns ++, ms ++){c *= ns; c /= ms;}
    	return c;
    }
}

import java.util.*;

class Chara {
    String name;
    ArrayList<Integer> appear;
    private int point;

    Chara(String name, ArrayList<Integer> appear) {
        this.name = name;
        this.appear = appear;
        this.point = 0;
    }

    void addPoint(int pt) {
        this.point += pt;
    }

    int getPoint() {
        return this.point;
    }
}

class Main {

    public static void main (String [] args) {

        Scanner sc = new Scanner(System.in);
        while(true) {
            int n = Integer.parseInt(sc.nextLine());
            if( n == 0 )    break;

            Chara [] charas = new Chara[n];
            for(int i = 0; i < n; i++) {
                String [] line = sc.nextLine().split(" ");
                ArrayList<Integer> appear = new ArrayList<Integer>();
                for(int j = 2; j < line.length; j++) {
                    appear.add(Integer.parseInt(line[j]));
                }
                charas[i] = new Chara(line[0], appear);
            }

            for(int t = 0; t < 30; t++) {
                int point = n + 1;
                for (Chara c : charas) {
                    if (c.appear.indexOf(t) != -1) {
                        point -= 1;
                    }
                }
                for (Chara c : charas) {
                    if (c.appear.indexOf(t) != -1) {
                        c.addPoint(point);
                    }
                }
            }

            ArrayList<Integer> points = new ArrayList<Integer>();
            for(Chara c : charas) {
                points.add(c.getPoint());
            }
            int min = Collections.min(points);

            ArrayList<String> minNames = new ArrayList<String>();
            for(Chara c : charas) {
                if (c.getPoint() == min) {
                    minNames.add(c.name);
                }
            }
            Collections.sort(minNames);

            System.out.printf("%d %s\n", min, minNames.get(0));
        }

    }
}

import java.util.*;

class Main{

    class Idle{
        String name;
        int m;
        int[] d;
        int point = 0;
        Idle(String name, int m, int[] d){
            this.name = name;
            this.m = m;
            this.d = d;
        }
    }

    void solve(){
        Scanner sc = new Scanner(System.in);

        while(true){
            int n = sc.nextInt();
            if(n==0) break;

            int[] cnt = new int[30];
            Arrays.fill(cnt, n+1);

            Idle[] idle = new Idle[n];
            for(int i=0; i<n; i++){
                String name = sc.next();
                int m = sc.nextInt();
                int[] d = new int[m];
                for(int j=0; j<m; j++){
                    d[j] = sc.nextInt();
                    cnt[d[j]]--;
                }
                idle[i] = new Idle(name, m, d);
            }

            for(int i=0; i<n; i++){
                int point = 0;
                for(int j=0; j<idle[i].m; j++){
                    point += cnt[idle[i].d[j]];
                }
                idle[i].point = point;
            }

            Arrays.sort(idle, new Comparator<Idle>(){
                    public int compare(Idle i1, Idle i2){
                        if(i1.point<i2.point) return -1;
                        if(i1.point>i2.point) return 1;
                        String n1 = i1.name, n2 = i2.name;
                        int len1 = n1.length(), len2 = n2.length();
                        for(int i=0; i<Math.min(len1, len2); i++){
                            if(n1.charAt(i)<n2.charAt(i)) return -1;
                            if(n1.charAt(i)>n2.charAt(i)) return 1;
                        }
                        if(len1<len2) return -1;
                        if(len1>len2) return 1;
                        return 0;
                    }
                });

            System.out.println(idle[0].point+" "+idle[0].name);
        }
    }

    public static void main(String[] args){
        new Main().solve();
    }
}

