				}
			}
			for(int i=0;i<31;i++){
				int p = N+1;
				for(int j=0;j<N;j++){
					if(c[j].time[i]==1){
						p--;
					}
				}
				for(int j=0;j<N;j++){
					if(c[j].time[i]==1){
