using System.Collections.Generic;
using System.Linq;
using System;

public class  Chara
{
    public string name { get; set; }
    public List<int> mv { get; set; }
    public int point { get; set; }
}

public class hello
{
    public static void Main()
    {
        while (true)
        {
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            var charas = new List<Chara>();
            var point = new int[30];
            for (int i = 0; i < n; i++)
            {
                string[] s = Console.ReadLine().Trim().Split(' ');
                var te = new Chara();
                te.mv = new List<int>();
                te.name = s[0];
                for (int j = 0; j < int.Parse(s[1]); j++)
                {
                    var temp2 = int.Parse(s[j + 2]);
                    te.mv.Add(temp2);
                    point[temp2]++;
                }
                charas.Add(te);
            }
            GetPoint(charas, point);
            var w1 = charas.Min(x => x.point);
            var ans = charas.Where(x => x.point == w1).OrderBy(y => y.name).First();
            Console.WriteLine("{0} {1}", ans.point, ans.name);
        }
    }
    public static void GetPoint(List<Chara> c, int[] p)
    {
        var n = c.Count();
        foreach (var x in c)
        {
            var pcount = 0;
            foreach (var y in x.mv)
                pcount += n - p[y] + 1;
            x.point = pcount;
        }
    }
}

