from collections import defaultdict
while 1:
    N = int(input())
    if N == 0:
        break
    D = defaultdict(list)
    S = [None]*N
    for i in range(N):
        name, _, *ts = input().split()
        S[i] = name
        for t in map(int, ts):
            D[t].append(i)
    P = [0]*N
    for vs in D.values():
        p = N+1 - len(vs)
        for v in vs:
            P[v] += p
    m = min(P)
    U = [S[i] for i in range(N) if P[i] == m]
    print(m, min(U))

