while True:
    try:
        n = int(input())
    except:
        break

    if n == 0: break

    char = []
    name = []
    times = []

    scores = {i: n + 1 for i in range(30)}

    for i in range(n):
        char = input().split()

        name.append(char[0])
        times.append(list(map(int, char[2:])))

        for t in times[i]:
            scores[t] = scores[t] - 1

    res = {}
    for i in range(n):
        res[i] = sum([scores[t] for t in times[i]])

    score = min(res.values())

    names = [name[i] for i in range(n) if score == res[i]]
    names.sort()
    print(score, names[0])

