void maketable(ll *dp, ll *sum, int w){

  memset(dp, 0, sizeof(dp));
  memset(sum, 0, sizeof(sum));
  dp[0] = 1;
  for(int i = 1; i < 100010; i++){
    for(int j = 1; j <= w; j++){
      if(i - j >= 0) dp[i] = (dp[i] + dp[i-j]) % mod;
    }
  }
  rep(i, 100010){
    sum[i] = dp[i];
    if(i > w) sum[i] = (sum[i] + sum[i-w]) % mod;
  }
}

