const ll M=100000;
ll dp5[100001],dp3[100001];

ll calc(bool is5, ll len){
  ll res=0;
  if(is5){
    repl(l,1,len+1){
      if((len-l)%5==0){
        (res+=dp5[l])%=mod;
      }
    }
  }else{
    repl(l,1,len+1){
      if((len-l)%3==0){
        (res+=dp3[l])%=mod;
      }
    }
  }
  return res;
}

