#define N 100000
#define M 100000007

int dp[N+2][2]={0};

void init(){
  int loop[2]={5,3};
  for(int t=0;t<2;t++){
    dp[0][t]=1;
    dp[1][t]=1;
    for(int i=2;i<=loop[t];i++)dp[i][t]=dp[i-1][t]*2;
    for(int i=loop[t]+1;i<N+2;i++){
      dp[i][t]=1;
      for(int j=1;j<=loop[t];j++){
	dp[i][t]+=dp[i-j][t];
      }
    }
  }
  return;
}

int main()
{
  string s;
  long long int ans;
  int i,cnt;
  init();
  while(cin>>s,s!="#"){
    ans=1;
    cnt=1;
    for(i=1;i<s.size();i++){
      if(s[i-1]==s[i]){
	cnt++;
      }else{
	int t;
	if(s[i-1]=='8'||s[i-1]=='0')t=1;
	else t=0;
	ans*=dp[cnt][t];
	cnt=1;
      }
    }
    i=s.size()-1;
    if(s[i-1]==s[i]){
      int t;
      if(s[i-1]=='8'||s[i-1]=='0')t=1;
      else t=0;
      ans*=dp[cnt][t];
    }
    cout<<ans%M<<endl;
  }
}

