typedef unsigned long long ull;

ull const mod = 100000007;
ull dp[1000000][6] = {};

ull divn(int n, int div){
    ull& res = dp[n][div];
    if (res) return res;

    res = 1;
    if (n == 1) return res;

    rep(i, min(div, n - 1)){
        res = res + divn(n - (i+1), div) % mod;
    }
    return res;
}

