using namespace std;
typedef long long ll;
typedef unsigned int uint;
typedef unsigned long long ull;
static const double EPS = 1e-9;
static const double PI = acos(-1.0);

#define REP(i, n) for (int i = 0; i < (int)(n); i++)
#define FOR(i, s, n) for (int i = (s); i < (int)(n); i++)
#define FOREQ(i, s, n) for (int i = (s); i <= (int)(n); i++)
#define FORIT(it, c) for (__typeof((c).begin())it = (c).begin(); it != (c).end(); it++)
#define MEMSET(v, h) memset((v), h, sizeof(v))

int memo[2][6][100010][5];
const ll MOD = 100000007;
char str[100010];

ll calc(int ok, int upper, int rest, int cont) {
  if (memo[ok][upper][rest][cont]) { return memo[ok][upper][rest][cont]; }
  if (rest == 0) { return 1; }
  ll ret = 0;
  if (ok && rest >= upper + 1) {
    assert(cont == 0);
    ret = (ret + calc(ok, upper, rest - upper, cont)) % MOD;
  }
  ret = (ret + calc(0, upper, rest - 1, 0)) % MOD;
  if (rest >= 2 && cont <= upper - 2) {
    ret = (ret + calc(0, upper, rest - 1, cont + 1)) % MOD;
  }
  return memo[ok][upper][rest][cont] = ret;
}

int main() {
  MEMSET(memo, 0);
  FOREQ(i, 1, 100000) {
    calc(1, 5, i, 0);
    calc(1, 3, i, 0);
  }
  memo[1][5][0][0] = 1;
  memo[1][3][0][0] = 1;
  while (scanf("%s", str), str[0] != '#') {
    int len = strlen(str);
    str[len++] = '$';
    int cnt = 0;
    int prev = -1;
    ll ans = 1;
    REP(i, len) {
      if (prev != str[i]) {
        //cout << cnt << " " << memo[1][cnt][0] << endl;
        int upper = 5;
        if (prev == '8' || prev == '0') { upper = 3; }
        ans = (ans * (ll)memo[1][upper][cnt][0]) % MOD;
        cnt = 0;
      }
      prev = str[i];
      cnt++;
    }
    printf("%lld\n", ans);
  }
}

