  REP(i,6,max_length)dp[1][i] = (((((dp[1][i-1] + dp[1][i-2])%MOD + dp[1][i-3])%MOD + dp[1][i-4])%MOD + dp[1][i-5])%MOD + 1ULL) % MOD;

  ull ans = 1;
  rep(i,vec.size())
