#include <bits/stdc++.h>
#define MOD (long long)(1e8 + 7)
using namespace std;

string s;

long long solve();
long long calc(long long len, long long msize);

int main() {
  while(1) {
    cin >> s;
    if(s == "#") break;
    cout << solve() << endl;
  }
  return 0;
}

long long solve() {
  long long ans = 1, ssize = s.size();
  for(int i = 0; i < ssize; ++i) {
    char now = s[i];
    long long cnt = 0, nowsize = 3;
    if(now != '0' && now != '8') nowsize += 2;
    while(i < ssize && s[i] == now) ++i, ++cnt;
    (ans *= calc(cnt, nowsize)) %= MOD;
    --i;
  }
  return ans;
}

long long calc(long long len, long long msize) {
  long long ans = 0;
  vector<long long> dp;
  dp.assign(msize, 0);
  dp[len % msize] = 1;
  for(int i = 0; i < len; ++i) {
    long long sum = 0;
    for(int j = 0; j < msize; ++j) (sum += dp[j]) %= MOD;
    if((len - 1 - i) % msize == 0) (ans += sum) %= MOD;
    dp[(len - 1 - i) % msize] = sum;
  }
  return ans;
}

