      }
      else{
        if(text[text_i] == '8' || text[text_i] == '0'){
          res = (res * dp[3][length]) % MOD;
          res %= MOD;
        }
        else{
          res = (res * dp[5][length]) % MOD;
          res %= MOD;
        }
        length = 1;
      }
    }

    if(length > 0){
      if(text[text.size() - 1] == '8' || text[text.size() - 1] == '0'){
        res = (res * dp[3][length]) % MOD;
        res %= MOD;
      }
      else{
        res = (res * dp[5][length]) % MOD;
        res %= MOD;
      }
    }
    printf("%lld\n",res % MOD);
  }
}

